var common = require('./common');


//@
//@ All commands run synchronously, unless otherwise stated.
//@

//@include ./cd
var _cd = require('./cd');
exports.cd = common.wrap('cd', _cd);

//@include ./pwd
var _pwd = require('./pwd');
exports.pwd = common.wrap('pwd', _pwd);

//@include ./ls
var _ls = require('./ls');
exports.ls = common.wrap('ls', _ls);

//@include ./find
var _find = require('./find');
exports.find = common.wrap('find', _find);

//@include ./cp
var _cp = require('./cp');
exports.cp = common.wrap('cp', _cp);

//@include ./rm
var _rm = require('./rm');
exports.rm = common.wrap('rm', _rm);

//@include ./mv
var _mv = require('./mv');
exports.mv = common.wrap('mv', _mv);

//@include ./mkdir
var _mkdir = require('./mkdir');
exports.mkdir = common.wrap('mkdir', _mkdir);

//@include ./test
var _test = require('./test');
exports.test = common.wrap('test', _test);

//@include ./cat
var _cat = require('./cat');
exports.cat = common.wrap('cat', _cat);

//@include ./to
var _to = require('./to');
String.prototype.to = common.wrap('to', _to);

//@include ./toEnd
var _toEnd = require('./toEnd');
String.prototype.toEnd = common.wrap('toEnd', _toEnd);

//@include ./sed
var _sed = require('./sed');
exports.sed = common.wrap('sed', _sed);

//@include ./grep
var _grep = require('./grep');
exports.grep = common.wrap('grep', _grep);

//@include ./which
var _which = require('./which');
exports.which = common.wrap('which', _which);

//@include ./echo
var _echo = require('./echo');
exports.echo = _echo; // don't common.wrap() as it could parse '-options'

//@include ./dirs
var _dirs = require('./dirs').dirs;
exports.dirs = common.wrap("dirs", _dirs);
var _pushd = require('./dirs').pushd;
exports.pushd = common.wrap('pushd', _pushd);
var _popd = require('./dirs').popd;
exports.popd = common.wrap("popd", _popd);

//@include ./ln
var _ln = require('./ln');
exports.ln = common.wrap('ln', _ln);

//@
//@ ### exit(code)
//@ Exits the current process with the given exit code.
exports.exit = process.exit;

//@
//@ ### env['VAR_NAME']
//@ Object containing environment variables (both getter and setter). Shortcut to process.env.
exports.env = process.env;

//@include ./exec
var _exec = require('./exec');
exports.exec = common.wrap('exec', _exec, {notUnix:true});

//@include ./chmod
var _chmod = require('./chmod');
exports.chmod = common.wrap('chmod', _chmod);



//@
//@ ## Non-Unix commands
//@

//@include ./tempdir
var _tempDir = require('./tempdir');
exports.tempdir = common.wrap('tempdir', _tempDir);


//@include ./error
var _error = require('./error');
exports.error = _error;



//@
//@ ## Configuration
//@

exports.config = common.config;

//@
//@ ### config.silent
//@ Example:
//@
//@ ```javascript
//@ var silentState = config.silent; // save old silent state
//@ config.silent = true;
//@ /* ... */
//@ config.silent = silentState; // restore old silent state
//@ ```
//@
//@ Suppresses all command output if `true`, except for `echo()` calls.
//@ Default is `false`.

//@
//@ ### config.fatal
//@ Example:
//@
//@ ```javascript
//@ config.fatal = true;
//@ cp('this_file_does_not_exist', '/dev/null'); // dies here
//@ /* more commands... */
//@ ```
//@
//@ If `true` the script will die on errors. Default is `false`.
